// App.js
import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [file, setFile] = useState(null);
  const [fileId, setFileId] = useState('');
  const [files, setFiles] = useState([]);

  const handleRegister = async () => {
    try {
      await axios.post('/register', { username, password });
      alert('User registered successfully.');
    } catch (error) {
      console.error(error.response.data);
    }
  };

  const handleLogin = async () => {
    try {
      await axios.post('/login', { username, password });
      setLoggedIn(true);
      alert('Login successful.');
    } catch (error) {
      console.error(error.response.data);
    }
  };

  const handleUpload = async () => {
    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('username', username);

      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setFileId(response.data);
      alert(`File uploaded successfully. Your file ID is: ${response.data}`);
    } catch (error) {
      console.error(error.response.data);
    }
  };

  const handleListFiles = async () => {
    try {
      const response = await axios.get(`/files/${username}`);
      setFiles(response.data);
    } catch (error) {
      console.error(error.response.data);
    }
  };

  const handleDeleteFile = async (fileId) => {
    try {
      await axios.delete(`/files/${username}/${fileId}`);
      setFiles(files.filter(file => file.fileId !== fileId));
      alert('File deleted successfully.');
    } catch (error) {
      console.error(error.response.data);
    }
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  return (
    <div>
      {!loggedIn ? (
        <div>
          <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button onClick={handleRegister}>Register</button>
          <button onClick={handleLogin}>Login</button>
        </div>
      ) : (
        <div>
          <input type="file" onChange={handleFileChange} />
          <button onClick={handleUpload}>Upload</button>
          <button onClick={handleListFiles}>List Files</button>
          <ul>
            {files.map(file => (
              <li key={file.fileId}>
                {file.fileName} <button onClick={() => handleDeleteFile(file.fileId)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
      {fileId && <div>Your file ID is: {fileId}</div>}
    </div>
  );
}

export default App;
