// UploadFile.js

import React, { useState } from 'react';

const UploadFile = () => {
    const [file, setFile] = useState(null);
    const [token, setToken] = useState('');

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleLogin = async () => {
        try {
            const response = await fetch('http://localhost:5000/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ username: 'user1', password: 'password1' }) // Replace with actual user credentials
            });
            const data = await response.json();
            setToken(data.token);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (!file) {
            alert('Please select a file.');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('file', file);
            formData.append('userId', token);

            const response = await fetch('http://localhost:5000/uploadfile', {
                method: 'POST',
                body: formData
            });

            const data = await response.json();

            console.log(data);
            alert('File uploaded successfully!');
        } catch (error) {
            console.error('Error:', error);
            alert('Failed to upload file.');
        }
    };

    return (
        <div>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-4'></div>
                    <div className='col-md-4 col-sm-12'>
                        <h2>File Upload</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <input className="form-control" type="file" id="formFileMultiple" onChange={handleFileChange} />
                            </div>
                            <button type="submit" className='btn btn-primary'>Upload</button>
                        </form>
                        <button onClick={handleLogin} className='btn btn-primary'>Login</button>
                    </div>
                    <div className='col-md-4'></div>
                </div>
            </div>
        </div>
    );
};

export default UploadFile;
